sequence.script:

var tipoAnnio = JSON.parse(mc.getProperty("TipoAnniooo"));

var datosString = mc.getPayloadJSON();

const añoActual = new Date().getFullYear();
var edad = añoActual - parseInt(datosString.anioNacimiento);

var informacion = {
    "Nombre": datosString.nombre,
    "Año de nacimiento": datosString.anioNacimiento,
    "Carrera": datosString.carrera,
    "Ciudad": datosString.entidad_federativa,
    "Edad": edad,
    "Tipo Ano": tipoAnnio.text
};

mc.setPayloadJSON(informacion);
